import axios from 'axios';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import { avatar } from '../../images';
import { UserInterface } from '../../types';

function UserView() {

   const {userid} = useParams(),
   [user , setUser]= useState<UserInterface>(),
   getUser = async ()=> {
                  try {
                  const res = await axios.get(`https://6270020422c706a0ae70b72c.mockapi.io/lendsqr/api/v1/users/${userid}`);
                    setUser(res.data);  
                    //console.log(res.data); 
                  } catch (error) {
                    console.log(error);

                  }
               }



               useEffect(() => {
                  getUser();

                  return ()=>{}
             },[])
  return (

                  <div className="container-fluid">
                               <Header />
                               <Sidebar />
                        <div className="dashboard">
                               <div className="col-md-12">
                                    <div className="row">
                                    <span className="dashboard_page_title">Users </span>
                                   </div>
                               </div>
                               <div className="m-4"></div>
                               <div className="row">
                               <div className="col-md-12">
                                    <div className="card">
                                        <div className=''>
                                            <div className='username_holder'> <img  className="userplace_holder" src={avatar} alt="brief"/></div> 
                                              <div className='username'>{user?.userName}</div>
                                        </div>
                                        <div className='col-md-3'>

                                        </div>
                                        <div className='col-md-3'>

                                        </div>

                                    </div>
                                </div>
                                 {/* <div className="mrg_top"> */}
                                        {/* <div className="card">
                                            <div className="col-md-12">
                                                <div className="">
                                                      <div>{user?.userName}</div>
                                                </div>
                                            </div>
                                        </div> */}
                                    {/* </div> */}   
                              </div>
                        </div>
                  </div>

  )
}

export default UserView
