import React, { forwardRef, useEffect , useState } from "react";
import axios from "axios";
import moment from "moment";
import Moredetails from '../images/moredetails.png';
import '../styles/Custom.css';
import { Dropdown } from "react-bootstrap";
import { userIcon } from "../images";
import { Link } from "react-router-dom";
import DataTable from "react-data-table-component";
import { UserInterface } from "../types";

const status = [{color: 'success', label:'active'}, {color: 'secondary', label:'inactive'}, {color: 'warning', label:'pending'}, {color: 'danger', label:'blacklisted'}]
const MoreToggle = forwardRef(({ onClick }:any, ref:any) => (

   <img className="px-3 py-1" src={Moredetails} alt="logo" ref={ref} onClick={(e) => {
       e.preventDefault();
       onClick(e);
     }} /> 
 ));

function UserListing() {


   const [records , setRecords]= useState<any[]>([]),
    [pending, setPending] = React.useState(true);

   const getRecords= async () =>{
      try {
         const res = await axios.get("https://6270020422c706a0ae70b72c.mockapi.io/lendsqr/api/v1/users");
        setRecords(res.data); 
        setPending(false); 
        //console.log(res.data); 
      } catch (error) {

      }
   }
   useEffect(() => {
                  getRecords();
             },[])
 return (


               <>
               <table className="table table_spacing">
                  <thead>
                     <tr className="table_text_header">
                        <th scope="col"> Organization </th>
                        <th scope="col">Username</th>
                        <th scope="col">Email</th>
                        <th scope="col">Phonenumber</th>
                        <th scope="col">Data Joined </th>
                        <th scope="col"> Status </th>
                     </tr>
                  </thead>
                  <tbody>
                     {records.map((record)=>{
                        const _status = status[Math.floor(Math.random() * 4)]

                        return(
                                       <tr className="table_text" key={record.id}> 
                                       <td className="table_text">{record.orgName}</td>
                                       <td>{record.userName}</td>
                                       <td>{record.email}</td>
                                       <td>{record.phoneNumber}</td>
                                       <td>{moment(record.createdAt).format('MMMM Do YYYY, h:mm a')}</td>
                                       <td>
                                          <span className={`badge rounded-pill bg-${_status.color} text-${_status.color} bg-opacity-10`}>{_status.label}</span>

                                          </td>
                                       <td>
                                          <Dropdown>
                                             <Dropdown.Toggle as={ MoreToggle } variant="transparent" id="dropdown-basic" />
                                             <Dropdown.Menu>
                                                <Link to={`/users/${record.id}`} className="d-block py-2 px-1">
                                                   <img width={20} src={userIcon} alt="userpro" className="mx-2"/> 
                                                   <small>View details</small>
                                                </Link>
                                                <Link to={`/users/${record.id}`} className="d-block py-2 px-1">
                                                   <img width={20} src={userIcon} alt="userpro" className="mx-2"/> 
                                                   <small>Blacklist User</small>
                                                </Link>
                                                <Link to={`/users/${record.id}`} className="d-block py-2 px-1">
                                                   <img width={20} src={userIcon} alt="userpro" className="mx-2"/> 
                                                   <small>Activate User</small>
                                                </Link>
                                                {/* <Link to={`/users/${record.id}`}><img src={userIcon} width={20} alt="logo" />  View Details</Link>
                                                <Link to="#/action-2"><img src={userIcon} width={20} alt="logo" />  Another action</Link>
                                                <Link to="#/action-3"><img src={userIcon} width={20} alt="logo" />  Something else</Link> */}
                                             </Dropdown.Menu>
                                          </Dropdown>                           
                                       </td>
                                       </tr>

                     )})}
                  </tbody>
               </table>
               <DataTable
            columns={[
               {
        sortable: true, name:'Organization'.toLocaleUpperCase(), selector:(row: UserInterface)=>row.orgName} ,
               {
        sortable: true, name:'Username'.toLocaleUpperCase(), selector:(row: UserInterface)=>row.userName},
               {
        sortable: true, name:'Email'.toLocaleUpperCase(), selector:(row: UserInterface)=>row.email},
               {
        sortable: true, name:'Phonenumber'.toLocaleUpperCase(), selector:(row: UserInterface)=>row.phoneNumber},
               {
        sortable: true, name:'Data Joined'.toLocaleUpperCase(), selector:(row: UserInterface)=>row.createdAt} ,
                {name:'Status'.toLocaleUpperCase(), selector:(row: UserInterface&{status:any})=>row.status()} ,
                {name:'', selector:(row: UserInterface&{status:any, action:any})=>row.action()} ,
            ]}
            data={records.map((_)=>{
               const _status = status[Math.floor(Math.random() * 4)]
               return ({..._, createdAt: moment(_.createdAt).format('MMMM Do YYYY, h:mm a'), status:() => <span className={`badge rounded-pill bg-${_status.color} text-${_status.color} bg-opacity-10`}>{_status.label}</span>, action: ()=>
                  <Dropdown>
                     <Dropdown.Toggle as={ MoreToggle } variant="transparent" id={`dropdown-${_.id}`} />
                     <Dropdown.Menu>
                        <Link to={`/users/${_.id}`} className="d-block py-2 px-1">
                           <img width={20} src={userIcon} alt="userpro" className="mx-2"/> 
                           <small>View details</small>
                        </Link>
                        <Link to={`/users/${_.id}`} className="d-block py-2 px-1">
                           <img width={20} src={userIcon} alt="userpro" className="mx-2"/> 
                           <small>Blacklist User</small>
                        </Link>
                        <Link to={`/users/${_.id}`} className="d-block py-2 px-1">
                           <img width={20} src={userIcon} alt="userpro" className="mx-2"/> 
                           <small>Activate User</small>
                        </Link>
                        {/* <Link to={`/users/${record.id}`}><img src={userIcon} width={20} alt="logo" />  View Details</Link>
                        <Link to="#/action-2"><img src={userIcon} width={20} alt="logo" />  Another action</Link>
                        <Link to="#/action-3"><img src={userIcon} width={20} alt="logo" />  Something else</Link> */}
                     </Dropdown.Menu>
                  </Dropdown>   })
            })} pagination progressPending={pending}
        />
                  </>
 );
 }
export default UserListing;
