import React, { useEffect , useState } from "react";
import axios from "axios";
import moment from "moment";
import Moredetails from '../images/moredetails.png';
import '../styles/Custom.css';
import { Dropdown } from "bootstrap";

const status = [{color: 'success', label:'active'}, {color: 'secondary', label:'inactive'}, {color: 'warning', label:'pending'}, {color: 'danger', label:'blacklisted'}]


function UserListing() {


   const [records , setRecords]= useState<any[]>([]);

   const getRecords= async () =>{
      try {
         const res = await axios.get("https://6270020422c706a0ae70b72c.mockapi.io/lendsqr/api/v1/users");
        setRecords(res.data);  
        //console.log(res.data); 
      } catch (error) {

      }
   }
   useEffect(() => {
                  getRecords();
             },[])
 return (


               <>
               <table className="table table_spacing">
                  <thead>
                     <tr className="table_text_header">
                        <th scope="col"> Organization </th>
                        <th scope="col">Username</th>
                        <th scope="col">Email</th>
                        <th scope="col">Phonenumber</th>
                        <th scope="col">Data Joined </th>
                        <th scope="col"> Status </th>
                     </tr>
                  </thead>
                  <tbody>
                     {records.map((record)=>{
                        const _status = status[Math.floor(Math.random() * 4)]

                        return(
                                       <tr className="table_text" key={record.id}> 
                                       <td className="table_text">{record.orgName}</td>
                                       <td>{record.userName}</td>
                                       <td>{record.email}</td>
                                       <td>{record.phoneNumber}</td>
                                       <td>{moment(record.createdAt).format('MMMM Do YYYY, h:mm a')}</td>
                                       <td>
                                          <span className={`badge rounded-pill bg-${_status.color} text-${_status.color} bg-opacity-10`}>{_status.label}</span>

                                          </td>
                                       <td></td>
                                       <td className="dropdown">

    <Dropdown>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Dropdown Button
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
        <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
        <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
                                       <a className="dropdown-toggle" type="button" id={`more-${record.id}`} data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">

                                          <img src={Moredetails} alt="logo" /> 
  </a>


                                          <div className="dropdown-menu" aria-labelledby={`more-${record.id}`}>
                                             <a className="dropdown-item" href="#">Action</a>
                                             <a className="dropdown-item" href="#">Another action</a>
                                             <a className="dropdown-item" href="#">Something else here</a>
                                          </div>

                                       </td>
                                       </tr>

                     )})}
                  </tbody>
               </table>
                  </>
 );
 }
export default UserListing;
